                <br></br>
                <label>notes</label>
                <input onChange={(event) => setNotes(event.target.value)} value={notes}></input>
                <br></br>
                <label>Attractions</label>
                <input onChange={(event) => setAttraction(event.target.value)} value={attractionState}></input>
            </form> */}
            <Button variant="success" onClick={handleSubmit}>Save</Button>
        </>

    function handleDelete() {
        fetch(`http://localhost:9292/locations/${id}`, {
            method: "DELETE"
        })
        setPlaces((currentPlaces) => currentPlaces.filter((item) => item.id !== id))
    }
    return (
        <div>
            <Link to={`/location/${id}/attractions`}> {city}, {state} - {country}
            </Link>
            <Button onClick={showEdit}>Edit</Button>
            <Button variant="danger" onClick={handleDelete}>Delete</Button>
            {editStatus ? editForm : <></>}

        </div>
    )
}
export default Location
