    function handleEdit() {
        fetch('http://localhost:9292/locations/:id', {
            method: "PATCH",
            headers: { "Content-Type": "applications/json" },
            body: JSON.stringify(
                {
                    "city": city,
                    "state": state,
                    "country": country,
                    "id": places[places.length - 1]["id"] + 1
                }
            )
        })
    }

    function handleDelete(){
        fetch(`http://localhost:9292/locations/${id}`,{
            method: "DELETE"
        })
        setPlaces((currentPlaces)=> currentPlaces.filter((item) => item.id !== id ))
    }
